def get_conf():
    content = open('config/gmailconnectivity.json')
    config = json.load(content)
    email = config['Username']
    password = config['Password']
    return email, password


def send_mail(body, sender, receiver, subject, email, password):
    message = MIMEMultipart()
    message['Subject'] = subject
    message['From'] = sender
    message['To'] = ", ".join(receiver)

    body_content = body
    message.attach(MIMEText(body_content, "html"))
    msg_body = message.as_string()

    # server = smtplib.SMTP('localhost')
    server = smtplib.SMTP_SSL('smtp.gmail.com')
    server.login(email, password)
    server.sendmail(sender, receiver, msg_body)
    #
    server.quit()


def transform_frame_to_html(all_bond, fx, all_indices, all_cmd, datacalendar, df_best, select_crypto_df):
    output1 = build_table(select_crypto_df, "blue_light")
    output2 = build_table(df_best.iloc[:5, :].reset_index().rename(columns={'index': 'symbol'}), "blue_light")
    output3 = build_table(all_bond, "blue_light")
    output4 = build_table(all_indices, "blue_light")
    output5 = build_table(fx, "blue_light")
    output6 = build_table(all_cmd, "blue_light")
    if type(datacalendar) != str:
        output7 = build_table(datacalendar, "blue_light")
    else:
        output7 = datacalendar
    return output1, output2, output3, output4, output5, output6, output7


