def get_standard_text(ls, txt, df, intro_tweet, api):
    for el in ls:
        try:
            text_el = f'- {el} is priced at {df[df["Symbol"] == el]["Today Price"].values[0]} ' \
                      f'with {"an increase" if list(df[df["Symbol"] == el]["Change(24h)"].values[0])[0] != "-" else "a decrease"} change of ' \
                      f'{df[df["Symbol"] == el]["Change(24h)"].values[0]} in the last 24h and ' \
                      f'has a market cap of {df[df["Symbol"] == el]["Market Cap"].values[0]} \n'
            txt += text_el
        except IndexError:
            pass
    try:
        threadi_tweet = api.update_status(status=txt,
                                          in_reply_to_status_id=intro_tweet.id,
                                          auto_populate_reply_metadata=True)
    except tweepy.errors.Forbidden:
        threadi_tweet = recurse_tweet(txt, api, intro_tweet)
    return threadi_tweet


def recurse_tweet(msg, api, intro_tweet):
    if len(msg) > 280:
        for i in range(280):
            if msg[280 - i] == '\n':
                split_position = i
                break
        txt1 = msg[:280 - split_position]
        threadi1_tweet = api.update_status(status=txt1,
                                           in_reply_to_status_id=intro_tweet.id,
                                           auto_populate_reply_metadata=True)
        txt2 = msg[280 - split_position + 1:]
        head = 'Continuation:\n'
        txt3 = head + txt2
        recurse_tweet(txt3, api, threadi1_tweet)
    else:
        threadi1_tweet = api.update_status(status=msg,
                                           in_reply_to_status_id=api.user_timeline()[0].id,
                                           auto_populate_reply_metadata=True)
    return threadi1_tweet


